create database team05;
use team05;
DROP TABLE IF EXISTS restaurant;
create table restaurant(
  rest_id int not null AUTO_INCREMENT,
  name varchar(100) UNIQUE,
  location varchar(100),
  country varchar(100),
  avg_price float,
  avg_rating float, 
  menu varchar(100),
  review_count int(100),
  primary key(rest_id)
);

DROP TABLE IF EXISTS user;
CREATE TABLE user (
    user_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    username VARCHAR(50) NOT NULL UNIQUE,
    pwd VARCHAR(255) NOT NULL,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP
);

DROP TABLE IF EXISTS award;
CREATE TABLE award(
    award_id int(11) AUTO_INCREMENT,
    award_name varchar(128) NOT NULL,
    rest_id int(11) NOT NULL,
    award_by varchar(100) NOT NULL,
    award_for varchar(255), 
    award_year int(10), 
    PRIMARY KEY(award_id),
    foreign key (rest_id) REFERENCES restaurant (rest_id)
);
DROP TABLE IF EXISTS review;
CREATE TABLE review (
    review_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    user_id INT(11) NOT NULL,
    rest_id INT(11) NOT NULL,
    content VARCHAR(300),
    rating INT(5),
    FOREIGN KEY(user_id) REFERENCES user(user_id) ON UPDATE CASCADE ON DELETE CASCADE,
    FOREIGN KEY(rest_id) REFERENCES restaurant(rest_id) ON UPDATE CASCADE ON DELETE CASCADE 
);


DROP TABLE IF EXISTS food;
CREATE TABLE food(
    food_id int(11) AUTO_INCREMENT,
    food_name varchar(128) NOT NULL,
    food_country varchar(128) NOT NULL,
    food_ingred varchar(500) NOT NULL,
    food_allergies varchar(128) NOT NULL,
    PRIMARY KEY(food_id)
);
